import React, { useState, useEffect } from 'react';
import { Link, withRouter } from 'react-router-dom';
import Pagination from '@material-ui/lab/Pagination';
import { Card, CardActionArea,  Grid,  makeStyles, AppBar, Tab } from '@material-ui/core';
import '../App.css';
const useStyles = makeStyles({
        appbar: {
            background:'#006600',
            marginLeft:'30px',
            marginRight:'30px',
            width: 'auto',
            marginBottom:'30px',
        },
        card: {
            maxWidth: 300,
            height: 'auto',
            marginLeft: '30px',
            marginRight: '30px',
            borderRadius: 5,
            border: '1px solid #1e8678',
            boxShadow: '0 19px 38px rgba(0,0,0,0.30), 0 15px 12px rgba(0,0,0,0.22);'
        },
	titleHead: {
		borderBottom: '1px solid #1e8678',
		fontWeight: 'bold'
	},
	grid: {
		flexGrow: 1,
		flexDirection: 'row'
	},
	media: {
		height: '100%',
		width: '100%'
	},
	button: {
		color: '#1e8678',
		fontWeight: 'bold',
		fontSize: 12
	},
	btnGroup: {
		marginTop: '50px'
	},
	pages: {
		textAlign: 'center',
		margin: '100px',
		display: 'flex',
		flexDirection: 'row',
		justifyContent: 'center',
	}
});

const PlayList=(props)=>{
    const classes = useStyles();
    const [ playList, setPlayList ] = useState(undefined);
    const [page, setPage] = useState(1);
    const [ total, setTotal] =  useState(undefined);
    const [isLast, setLast] = useState(false);
    const [isFirst, setFirst] = useState(true);
    const [loading, setLoading] = useState(true);
    const [error, setError]  =  useState(false);

    async function getData(url, type){
        var request = require('request');
        var client_id = 'c9b1db19becd48f78d6913a3431ae24a'; // Your client id
        var client_secret = '98145905039345c4a5df9542ca98cc55'; // Your secret
        var authOptions = {
            url: 'https://accounts.spotify.com/api/token',
            headers: {
              'Authorization': 'Basic ' + (new Buffer(client_id + ':' + client_secret).toString('base64'))
            },
            form: {
              grant_type: 'client_credentials'
            },
            json: true
        };
        request.post(authOptions,function(error, response,body){
            if (!error && response.statusCode === 200) {
                    var token = body.access_token;
                    var options = {
                        url: url,
                        headers: {
                            'Authorization': 'Bearer ' + token
                        },
                        json: true
                    };
                    request.get(options, function(error, response, body) {
                    if(error ||  response.statusCode  ===  404){
                        setError(true);
                        setLoading(false);
                    }else{
                        if(type === 'playlists'){
                            setPlayList(body.playlists.items);
                            setTotal(Math.ceil(body.playlists.total / 24))
                        }
                        setLoading(false);
                    }
                    });
            }
        });
    }

    useEffect(()=> {
        async function fetchData () {
            try{

                await getData(`https://api.spotify.com/v1/browse/categories/${props.match.params.categories}/playlists?limit=24&&offset=${page*24-24}`,'playlists');
                if (page === 1) {
					setFirst(true);
					setLast(false);
				} else {
					setFirst(false);
                }
                if (page === total) {
					setLast(true);
					setFirst(false);
				} else {
					setLast(false);
                }
            } catch (e) {
                console.log(e);
                setError(true);
            }
        }

        fetchData();
    }, [page]);

    const changePageHandle = (event, page) => {
        setPage(page);
        if (page === 1) {
            setFirst(true);
        } else {
            setFirst(false);
        };
        if (page === total) {
            setLast(true);
        } else {
            setLast(false);
        }
    }

    const buildCard = (playlist)  => {
        return(
            <Grid item xs={12} sm={4} md={3} lg={2} xl={2}  key={playlist.id}>
                <Card className={classes.card} variant='outlined'>
                <CardActionArea>
                    <Link to={`/playlists/songsList/${playlist.id}`}>
                    <div className='category-image'>
                        <img alt="category" src={playlist.icons ? playlist.icons[0].url : playlist.images[0].url} width='100%'/>

                         <p className='category-name'>{playlist.name}</p>

                    </div>
                    </Link>
                </CardActionArea>
                </Card>
            </Grid>
        );
    };
    let card = playList && playList.map((playlist) => {
        return buildCard(playlist);
    });


    if(loading){
        return (
			<div>
				<h2>Loading....</h2>
			</div>
		);
    }else if(error){
        return (
			<div>
				<h2>Error....</h2>
			</div>
		);
    }else {
        return(
            <div>
                <AppBar position="static" className={classes.appbar}>
                    <Tab  label="All PlayLists" />
                    </AppBar>
                <Grid container className={classes.grid} spacing={2}>
                        {card}
                </Grid>
                {total === 1?<div className={classes.pages}>No More</div>:
                <div className={classes.pages}>
                        <Pagination count={total} page={page} defaultPage={page} onChange={changePageHandle} variant="outlined" shape="rounded" hidePrevButton={isFirst} hideNextButton={isLast} />
                </div>
                }
            </div>

        );
    }
}
export default withRouter(PlayList);
